// BLANK[1] ヒント：star 関数をここにコピーして、 draw 内で ellipse の代わりに使おう
function star(cx, cy, r){
beginShape(); // 点つなぎを始める
for(let i = 0; i < 5; i++){
const theta = TWO_PI * i * 2 / 5 - HALF_PI;
const x = cx + cos(theta) * r;
const y = cy + sin(theta) * r;
vertex(x, y); // 次につなぐ点を１つ増やす
}
endShape(CLOSE); // 点つなぎを終わる
}

